import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import { wrap } from "module";

export const useStyles = makeStyles(theme => ({
  root: {
    fle: 1,
    flexGrow: 1,
    fontSize: 15
  },
  paper: {
    alignItems: "stretch",
    display: "flex",
    flexDirection: " column",
    flexWrap: "nowrap",
    backgroundColor: "#fff",
    border: "none",
    borderRadius: ".625rem",
    boxShadow:
      "0 2px 0 rgba(90,97,105,.11), 0 4px 8px rgba(90,97,105,.12), 0 10px 10px rgba(90,97,105,.06), 0 7px 70px rgba(90,97,105,.1);"
  },
  img: {
    backgroundSize: "cover",
    backgroundPosition: "50%",
    position: "relative",
    minHeight: "10.3125rem",
    borderTopLeftRadius: ".625rem",
    borderTopRightRadius: ".625rem",
    backgroundRepeat: "no-repeat"

    // display: "flex",
    // alignItems: "stretch",
    // backgroundSize: "cover",
    // backgroundPosition: "50%",
    // position: "relative",
    // minHeight: "10.3125rem",
    // borderTopLeftRadius: "20px",
    // borderTopRightRadius: "20px",
    // backgroundRepeat: "no-repeat"
  },
  category: {
    color: "#fff",
    // fontSize: 14,
    top: ".9375rem",
    right: ".9375rem",
    position: "absolute",
    textTransform: "uppercase",
    // ${theme => (props.category === "PDF" ? "backgroundColor: #212529" : "backgroundColor: #fff000")}

    borderRadius: "10rem",
    padding: ".375rem .5rem"
  },

  title: {
    color: "#3d5170",
    fontSize: "2.7em",
    lineHeight: "1em",
    fontWeight: "bold",
    marginBottom: ".75rem"
  },
  desc: {
    color: "#5a6169;",
    fontSize: "20px",
    lineHeight: "22px",
    fontWeight: "500",
    height: "82px"
    // overflow: "hidden"
  },
  date: {
    width: 300,
    height: 100,
    color: "#5a6169;",
    fontSize: "16px",
    marginTop: "auto"
  },
  author: {
    display: "block",
    width: "60px",
    borderRadius: "50%",
    position: "absolute",
    top: "50px",
    zIndex: "100"
  }
}));

export const CardBody = styled.div`
  padding: 2.1875rem 1.5625rem;
  overflow: hidden;
  /* width: 200px;
  flex: 1; */

  /* background-color: #fff; */
  /* padding: 30px 20px; */
  /* margin: 30px 0;
  border-radius: 20px; */
  /* display: flex;
  flex-direction: column;
  align-items: stretch;
  flex-wrap: nowrap; */
`;
