import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";
import { typography, lineHeight, fontWeight } from "@material-ui/system";

export const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing(2),
    margin: "auto",
    maxWidth: 500
  },
  image: {
    width: 128,
    height: 128
  },
  img: {
    margin: "auto",
    display: "block",
    maxWidth: "100%",
    maxHeight: "100%"
  },
  h1: {
    color: "#3d5170",
    fontSize: "30px",
    lineHeight: "35px",
    fontWeight: "bold"
  },
  p: {
    color: "#5a6169;",
    fontSize: "20px",
    lineHeight: "22px",
    fontWeight: "500"
  }
}));

export const Content = styled.article`
  /* width: 200px;
  flex: 1; */

  background-color: #fff;
  /* padding: 30px 20px; */
  margin: 30px 0;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  align-items: stretch;
  /* flex-wrap: nowrap; */
  cursor: pointer;

  box-shadow: 0 2px 0 rgba(90, 97, 105, 0.11), 0 4px 8px rgba(90, 97, 105, 0.12),
    0 10px 10px rgba(90, 97, 105, 0.06), 0 7px 70px rgba(90, 97, 105, 0.1);
`;
