// import styled from "styled-components";
import { makeStyles } from "@material-ui/core/styles";

export const useStyles = makeStyles(theme => ({
  root: {
    fle: 1,
    flexGrow: 1,
    fontSize: 15
  },
  h1: {
    color: "#3d5170",
    fontSize: "20px",
    lineHeight: "1em",
    fontWeight: "bold",
    margin: "60px 0"
  },
  h2: {
    color: "#3d5170",
    fontSize: "2.7em",
    lineHeight: "1em",
    fontWeight: "500",
    marginBottom: ".75rem"
  }
}));
